import react, { useReducer } from "react";
import CartContext from "./CartContext";

// empty cart state to use as default value for state
const defaultCartState = {
  items: [],
  totalAmount: 0,
};

// reducer dispatch function to handle state
const cartReducer = (state, action) => {
  // if we add an item from the inputMealForm
  if (action.type === "ADD") {
    // concat creates a new array unlike push so u add items to new array, dont edit old state snapshot
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    // arg of findIndex must be a function that index if match found (return true) and -1 if false returned
    const existingCartItemIndex = state.items.findIndex((item) => {
      // essentially the condition to return index if true
      return item.id === action.item.id;
    });
    // if Item not in cart yet existing Cart Item will be nul;;
    const existingCartItem = state.items[existingCartItemIndex];

    let updatedItem;
    let updatedItems;

    // if item already in cart
    if (existingCartItem) {
      updatedItem = {
        ...existingCartItem,
        // updates existing amount
        amount: existingCartItem.amount + action.item.amount,
      };
      // items refers to the array of objects
      updatedItems = [...state.items];
      // update the item selected in the existing state array
      updatedItems[existingCartItemIndex] = updatedItem;
    }

    // item added first time
    else {
      updatedItem = { ...action.item };
      updatedItems = state.items.concat(updatedItem);
    }

    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }

  if (action.type === "REMOVE") {
  }
};

// main component in file
const CartContextProvider = (props) => {
  // store items state in a reducer function
  const [cartState, dispatchFunction] = useReducer(
    cartReducer,
    defaultCartState
  );

  // update cart state (addition) with arg of item to be added including quantity
  const addItemToCartHandler = (obj) => {
    dispatchFunction({ type: "ADD", item: obj });
  };

  // remove 1 item from cart at a time base on id
  const removeItemFromCartHandler = (id) => {
    dispatchFunction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartContextProvider;
