import React, { useContext }from 'react';

import classes from './Header.module.css';

import Meal from './meals.jpeg';

import HeaderCart from '../HeaderCart/HeaderCart';
import Cart from '../Cart/Cart';

import CartToggleContext, { CartToggleContextProvider } from '../CartToggleContext/CartToggleContext'



const Header = (props) => {
    // ctx to manage whether cart open or close
    const ctx = useContext(CartToggleContext);

    return (
        <CartToggleContextProvider>
            {/* cart component to store modal and cart info */}
            <Cart></Cart>
            <header className={classes['header']}>
                <h1 onClick={() => {window.location.reload();}}>CoolMeals</h1>
                {/* press to open Cart */}
                <HeaderCart onClick={ctx.toggleCartOpen}></HeaderCart>
            </header>
            <div className={classes['main-image']}>
                <img src={Meal} alt={''}></img>
            </div>
        </CartToggleContextProvider>
    )
};

export default Header;
