import React, { useState } from "react";
import Card from "../UI/Card/Card";
import FoodItem from "../FoodItem/FoodItem";

const Food = (props) => {
    // arr of foods stored in a state so that the quantity can be updated
  const [foodList, setFoodList] = useState([
    {
      food: "Sushi",
      details: "Finest fish and veggies",
      price: "22.99",
      quantity: 1,
    },
    {
      food: "Schnitzel",
      details: "A german speciality!",
      price: "16.50",
      quantity: 1,
    },
    {
      food: "Barbecue Burger",
      details: "American, raw, meaty",
      price: "12.99",
      quantity: 1,
    },
    {
      food: "Green Bowl",
      details: "Healthy...and green...",
      price: "18.99",
      quantity: 1,
    },
  ]);

  // function to retrieve food name and compare against food then add the input
  const retrieveDetails = (quantityToAdd, foodName) => {
    console.log(quantityToAdd);
    console.log(foodName);
  }


  return (
    <Card>
      <ul>
        {foodList.map((item) => {
          return (
            <FoodItem
              food={item.food}
              details={item.details}
              price={item.price}
              quantity={item.quantity}
              key={Math.random().toString()}
              retrieve={retrieveDetails}
            ></FoodItem>
          );
        })}
      </ul>
    </Card>
  );
};

export default Food;
