  items: [],
  totalAmount: 0,
  addItem: (item) => {},
  removeItem: (id) => {}
});

// default cart state
const defaultCartstate = {
  items: {},
  totalAmount: 0
}

// why we update the state dont immediately affect the cartheaderbutton quantity?
export const FoodContextProvider = (props) => {

  // make a dynamic state to manage the state of the cart

