import React, { useState }from 'react';

import classes from './Header.module.css';

import Meal from './meals.jpeg';

import HeaderCart from '../HeaderCart/HeaderCart';
import Cart from '../Cart/Cart';



const Header = (props) => {
    const [cartModalOpen, setCartModalOpen] = useState(false);

    // opens Cart
    const toggleCartOpen = () => {
        setCartModalOpen(true);
    };

    // closes Cart
    const toggleCartClose = () => {   
        setCartModalOpen(false)
    };

    console.log(cartModalOpen);

    return (
        <React.Fragment>
            <Cart openModal={cartModalOpen}></Cart>
            <header className={classes['header']}>
                <h1 onClick={() => {window.location.reload();}}>CoolMeals</h1>
                {/* press to open Cart */}
                <HeaderCart onClick={toggleCartOpen}></HeaderCart>
            </header>
            <div className={classes['main-image']}>
                <img src={Meal} alt={''}></img>
            </div>
        </React.Fragment>
    )
};

export default Header;
