import React, { useContext } from "react";
import CartToggleContext from "../../Header+Cart/CartToggleContext/CartToggleContext";
import classes from "./CartModal.module.css";
import Card from "../Card/Card";
import FoodContext from "../../FoodPortion/FoodContext/FoodContext";
import CartItem from "../../Header+Cart/CartItem/CartItem";

const CartModal = (props) => {
  const foodctx = useContext(FoodContext);
  const cartctx = useContext(CartToggleContext);
  const CartList = foodctx.FoodList;
  console.log(CartList);

  const TotalCost = (CartList) => {
    var cost = 0;
    for (let i in CartList) {
      cost += +CartList[i].Price;
    }
    return cost;
  }

  return (
    <React.Fragment>
      <div
        className={classes["backdrop"]}
        onClick={cartctx.toggleCartClose}
      ></div>
      <Card className={classes["modal"]}>
        {CartList.map((item) => {
          if (item.Quantity > 0) {
            return <CartItem key={"Other"+item.id} id={item.id} Quantity={item.Quantity} Price={item.Price} Food={item.food}></CartItem>;
          }
          else {
            return null;
          }
        })}
        <div className={classes["cartModalBottom"]}>
          <h2>Total Amount</h2>
          <div className={classes.right}>
            <h2 className={classes["totalMoney"]}>${TotalCost}</h2>
            <div>
              <button className={classes.close}>Close</button>
              <button className={classes.order}>Order</button>
            </div>
          </div>
        </div>
      </Card>
    </React.Fragment>
  );
};

export default CartModal;
