import React from "react";
import classes from "./Header.module.css";
import Meal from "./meals.jpeg";
import HeaderCart from "./HeaderCart";
import Cart from "../Cart/Cart";
import { useState } from "react";

import { CartToggleContextProvider } from "../CartToggleContext/CartToggleContext";

const Header = (props) => {
  // state to control cart open/close
  const [cartModalOpen, setCartModalOpen] = useState(false);

  // opens Cart
  const toggleCartOpen = () => {
    setCartModalOpen(true);
  };

  // closes Cart
  const toggleCartClose = () => {
    setCartModalOpen(false);
  };

  return (
    <CartToggleContextProvider>
      {/* cart component to store modal and cart info */}
      {<Cart></Cart> && cartModalOpen}
      <header className={classes["header"]}>
        <h1
          onClick={() => {
            window.location.reload();
          }}
        >
          CoolMeals
        </h1>
        {/* press to open Cart */}
        <HeaderCart openCart={toggleCartOpen}></HeaderCart>
      </header>
      <div className={classes["main-image"]}>
        <img src={Meal} alt={""}></img>
      </div>
    </CartToggleContextProvider>
  );
};

export default Header;
