import React, { useState } from "react";

// to retrieve food & quantity to be added from FoodItem component to Cart component
const FoodContext = React.createContext({
  updateFoodToAdd: (FoodId, QuantityToAdd) => {},
  FoodToAdd: { id: 0 },
});

export const FoodContextProvider = (props) => {
  const [FoodToAdd, setFoodToAdd] = useState(
    { "01": 0 },
    { "02": 0 },
    { "03": 0 },
    { "04": 0 }
  );

  // function to update FoodToAdd state
  const updateFoodToAdd = (FoodId, QuantityToAdd) => {
    setFoodToAdd((prev) => {
      // add quantity to be added to cart
      for (var i in prev) {
        if (FoodId == i) {
          prev[i] += QuantityToAdd;
        }
      }
      console.log(FoodToAdd);
    });
  };

  return (
    <FoodContext.Provider
      value={{
        updateFoodToAdd: updateFoodToAdd,
        FoodToAdd: FoodToAdd
      }}
    >
      {props.children}
    </FoodContext.Provider>
  );
};
