import React, {  useContext} from "react";
import Card from "../../UI/Card/Card";
import FoodItem from "../FoodItem/FoodItem";
import FoodContext from "../FoodContext/FoodContext";

import classes from "./Food.module.css";

const Food = (props) => {
  // context to get function to retrieve food id and value
  const ctx = useContext(FoodContext);

  // arr of foods to be mapped out into item components
  const foodList = [
    {
      food: "Sushi",
      details: "Finest fish and veggies",
      price: "22.99",
      id: "A1",
    },
    {
      food: "Schnitzel",
      details: "A german speciality!",
      price: "16.50",
      id: "A2",
    },
    {
      food: "Barbecue Burger",
      details: "American, raw, meaty",
      price: "12.99",
      id: "A3",
    },
    {
      food: "Green Bowl",
      details: "Healthy...and green...",
      price: "18.99",
      id: "A4",
    },
  ];

  // function to retrieve food name and compare against food then add the input
  const retrieveDetails = (quantityToAdd, foodId) => {
    ctx.updateFoodToAdd(foodId, quantityToAdd)
  };

  return (
    <Card className={classes.CardAddOn}>
      <ul>
        {foodList.map((item) => {
          return (
            <FoodItem
              food={item.food}
              details={item.details}
              price={item.price}
              quantity={item.quantity}
              key={item.id}
              retrieve={retrieveDetails}
              id={item.id}
            ></FoodItem>
          );
        })}
      </ul>
    </Card>
  );
};

export default Food;
