import FoodItem from "../FoodItem/FoodItem";
import Card from "../../UI/Card/Card";
import classes from "./FoodAvailable.module.css";
import { useContext } from "react";

const foodList = [
  {
    food: "Sushi",
    details: "Finest fish and veggies",
    id: "A1",
    Quantity: 0,
    Price: 22.99,
  },
  {
    food: "Schnitzel",
    details: "A german speciality!",
    id: "A2",
    Quantity: 0,
    Price: 16.5,
  },
  {
    food: "Barbecue Burger",
    details: "American, raw, meaty",
    id: "A3",
    Quantity: 0,
    Price: 12.99,
  },
  {
    food: "Green Bowl",
    details: "Healthy...and green...",
    id: "A4",
    Quantity: 0,
    Price: 18.99,
  },
];

const foodToShow = foodList.map((item) => {
  return (
    <FoodItem
      food={item.food}
      details={item.details}
      price={item.Price}
      quantity={item.quantity}
      key={item.id}
      //   retrieve={retrieveDetails}
      id={item.id}
    ></FoodItem>
  );
});

// function to retrieve food name and compare against food then add the input
//  const retrieveDetails = (quantityToAdd, foodId) => {
//    ctx.updateFoodList(foodId, quantityToAdd);
//    // pass change to most parent component to then pass to other components in other branch.
//  };

const FoodAvailable = (props) => {
  <Card className={classes.CardAddOn}>
    <ul></ul>
  </Card>;
};

export default FoodAvailable;
